INSERT INTO `cmf_index` VALUES ('3', '模块三', '四大教研组 汇聚行业权威专家', '高考命题方向的把脉者', 'admin/20180903/98f9d171de8a1a4640bce266725aecf6.png', '{\"append\":[{\"name\":\"魅力语文组\",\"value\":\"打基础、抓考点、练技能、冲写作，把握各年级重难点和命题趋势，让学生快速掌握考试技巧和得高分技能，以中高考满分作文为标杆，锻造写作高手。\",\"thumbnail\":\"admin\\/20180913\\/79b28423cdc094ce687c472104e3de06.png\"},{\"name\":\"金牌数学组\",\"value\":\"锁定核心知识点，用最简单高效的方法，让学生把知识点学透、学精、学准，确保考试不错丢一分，注重培养学生的学习习惯、数学思维、解题技巧，让学习成绩成倍提升。\",\"thumbnail\":\"admin\\/20180913\\/39adbc045ac87b0e5e531a7499293f04.png\"},{\"name\":\"考点英语组\",\"value\":\"特级教师、小升初、中高考骨干教师领衔，有着多年教学经验的名师担纲授课，在把握考纲、考点的同时，引爆学习潜能，教会学生高效学法，从而爱上英语、爱上学习。\",\"thumbnail\":\"admin\\/20180913\\/c9bb3b2eaa857fd1a4ba32f2407a4453.png\"},{\"name\":\"满分理化组\",\"value\":\"注重理化学科知识体系梳理，以及知识点之间的相互联系，把理化考点一一剖析，逐一传授每个考点的解题方法，精讲典型题，精炼类型题，寓教于乐。轻松冲刺满分。\",\"thumbnail\":\"admin\\/20180913\\/535fb93f0a43c08cc1acd02f1bbe276e.png\"}],\"video\":{\"file_urls\":\"\",\"file_names\":\"\"}}', '2018-09-03 10:25:27', '0', 'index', 'admin');
